import React, { useState, useEffect } from 'react';
import './App.css';

const API_URL = 'http://localhost:8080/tasks';

function App() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    status: 'todo'
  });

  // Carrega as tarefas quando o componente monta
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await fetch(API_URL);
      if (!response.ok) throw new Error('Erro ao carregar tarefas');
      const data = await response.json();
      setTasks(data || []);
    } catch (err) {
      setError('N√£o foi poss√≠vel carregar as tarefas');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (editingTask) {
        // Atualiza tarefa existente
        const response = await fetch(`${API_URL}/${editingTask.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });
        if (!response.ok) throw new Error('Erro ao atualizar tarefa');
      } else {
        // Cria nova tarefa
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });
        if (!response.ok) throw new Error('Erro ao criar tarefa');
      }

      // Reseta o formul√°rio
      setFormData({ title: '', description: '', status: 'todo' });
      setShowForm(false);
      setEditingTask(null);
      fetchTasks();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Tem certeza que deseja excluir esta tarefa?')) return;

    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Erro ao excluir tarefa');
      fetchTasks();
    } catch (err) {
      setError('Erro ao excluir tarefa');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (task) => {
    setEditingTask(task);
    setFormData({
      title: task.title,
      description: task.description,
      status: task.status
    });
    setShowForm(true);
  };

  const handleMove = async (task, newStatus) => {
    setLoading(true);
    try {
      const response = await fetch(`${API_URL}/${task.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...task, status: newStatus })
      });
      if (!response.ok) throw new Error('Erro ao mover tarefa');
      fetchTasks();
    } catch (err) {
      setError('Erro ao mover tarefa');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const filterTasksByStatus = (status) => {
    return tasks.filter(task => task.status === status);
  };

  const cancelForm = () => {
    setShowForm(false);
    setEditingTask(null);
    setFormData({ title: '', description: '', status: 'todo' });
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Kanban - Gerenciador de Tarefas</h1>
        <button 
          className="btn-primary"
          onClick={() => setShowForm(!showForm)}
          disabled={loading}
        >
          {showForm ? 'Cancelar' : '+ Nova Tarefa'}
        </button>
      </header>

      {error && <div className="error-message">{error}</div>}
      {loading && <div className="loading">Carregando...</div>}

      {showForm && (
        <div className="task-form">
          <h2>{editingTask ? 'Editar Tarefa' : 'Nova Tarefa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>T√≠tulo *</label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                required
                placeholder="Digite o t√≠tulo da tarefa"
              />
            </div>
            <div className="form-group">
              <label>Descri√ß√£o</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Descri√ß√£o opcional"
                rows="3"
              />
            </div>
            <div className="form-group">
              <label>Status</label>
              <select
                value={formData.status}
                onChange={(e) => setFormData({ ...formData, status: e.target.value })}
              >
                <option value="todo">A Fazer</option>
                <option value="progress">Em Progresso</option>
                <option value="done">Conclu√≠da</option>
              </select>
            </div>
            <div className="form-actions">
              <button type="submit" className="btn-primary" disabled={loading}>
                {editingTask ? 'Salvar Altera√ß√µes' : 'Criar Tarefa'}
              </button>
              <button type="button" className="btn-secondary" onClick={cancelForm}>
                Cancelar
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="kanban-board">
        <Column
          title="A Fazer"
          status="todo"
          tasks={filterTasksByStatus('todo')}
          onEdit={handleEdit}
          onDelete={handleDelete}
          onMove={handleMove}
        />
        <Column
          title="Em Progresso"
          status="progress"
          tasks={filterTasksByStatus('progress')}
          onEdit={handleEdit}
          onDelete={handleDelete}
          onMove={handleMove}
        />
        <Column
          title="Conclu√≠das"
          status="done"
          tasks={filterTasksByStatus('done')}
          onEdit={handleEdit}
          onDelete={handleDelete}
          onMove={handleMove}
        />
      </div>
    </div>
  );
}

function Column({ title, status, tasks, onEdit, onDelete, onMove }) {
  return (
    <div className="column">
      <div className="column-header">
        <h2>{title}</h2>
        <span className="task-count">{tasks.length}</span>
      </div>
      <div className="column-content">
        {tasks.length === 0 ? (
          <p className="empty-message">Nenhuma tarefa aqui</p>
        ) : (
          tasks.map(task => (
            <TaskCard
              key={task.id}
              task={task}
              onEdit={onEdit}
              onDelete={onDelete}
              onMove={onMove}
            />
          ))
        )}
      </div>
    </div>
  );
}

function TaskCard({ task, onEdit, onDelete, onMove }) {
  const getNextStatus = () => {
    if (task.status === 'todo') return 'progress';
    if (task.status === 'progress') return 'done';
    return null;
  };
  const getPrevStatus = () => {
    if (task.status === 'done') return 'progress';
    if (task.status === 'progress') return 'todo';
    return null;
  };
  const nextStatus = getNextStatus();
  const prevStatus = getPrevStatus();

  return (
    <div className="task-card">
      <div className="task-header">
